import React from "react";
import {
  MapContainer,
  TileLayer,
  Polyline,
  Marker,
  Popup,
} from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import { Stop } from "@/types/stop";
import { LiveBusLocation } from "@/types/delay";
import bus_icon_url from "../../../assets/Bus.png"; // rename to _url
import location_icon_url from "../../../assets/Location.png";
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete (L.Icon.Default.prototype as any)._getIconUrl;

L.Icon.Default.mergeOptions({
  iconUrl: markerIcon,
  iconRetinaUrl: markerIcon2x,
  shadowUrl: markerShadow,
});

interface RouteMapProps {
  stops: Stop[];
  busLocations?: LiveBusLocation[];
  pathCoordinates?: [number, number][];
}

// Create the custom icons
const busIcon = L.icon({
  iconUrl: bus_icon_url,
  iconRetinaUrl: bus_icon_url,
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

const locationIcon = L.icon({
  iconUrl: location_icon_url,
  iconRetinaUrl: location_icon_url,
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

const RouteMap: React.FC<RouteMapProps> = ({
  stops,
  busLocations = [],
  pathCoordinates,
}) => {
  const center: [number, number] = stops.length
    ? [stops[0].latitude, stops[0].longitude]
    : [33.7756, -84.3963];

  return (
    <MapContainer
      center={center}
      zoom={15}
      style={{ height: "500px", width: "100%" }}
    >
      <TileLayer
        attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a>'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {pathCoordinates && (
        <Polyline positions={pathCoordinates} color="#b3a369" />
      )}
      {stops.map((stop) => (
        <Marker
          key={stop.id}
          position={[stop.latitude, stop.longitude]}
          icon={locationIcon}
        >
          <Popup>{stop.name}</Popup>
        </Marker>
      ))}
      {busLocations.map((bus) => (
        <Marker
          key={bus.busId}
          position={[bus.latitude, bus.longitude]}
          icon={busIcon}
        >
          <Popup>
            Bus {bus.busId} on Route {bus.routeId}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default RouteMap;
