import React from "react";
import { useState, useEffect } from "react";
import { Button, Dropdown, DropdownButton } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "../../components/dropdown.css";

const Home: React.FC = () => {
  const busRoutes = {
    "Route 1": ["Stop A", "Stop B", "Stop C"],
    "Route 2": ["Stop D", "Stop E", "Stop F"],
    "Route 3": ["Stop G", "Stop H", "Stop I"],
  };

  const [selectedRoute, setSelectedRoute] = useState<string | null>(null);
  const [selectedStop, setSelectedStop] = useState<string | null>(null);

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Georgia Tech Stinger</h1>
      <h1>Shuttle Delay Visualizer</h1>
      <p style={{ fontSize: "18px", marginBottom: "20px" }}>
        Select a route and stop to visualize the shuttle delay information.
      </p>
      <div className="frame">
        <div className="div">
          <label htmlFor="route-select" className="medium">
            Choose your bus line:
          </label>
          <select
            id="route-select"
            onChange={(e) => {
              setSelectedRoute(e.target.value);
              setSelectedStop(null);
            }}
            value={selectedRoute || ""}
            className="medium"
          >
            <option value="" disabled>
              Select a route
            </option>
            {Object.keys(busRoutes).map((route) => (
              <option key={route} value={route} className="option">
                {route}
              </option>
            ))}
          </select>
        </div>
        {selectedRoute && (
          <div className="div-2">
            <label htmlFor="stop-select" className="medium">
              Current stop:
            </label>
            <select
              id="stop-select"
              onChange={(e) => setSelectedStop(e.target.value)}
              value={selectedStop || ""}
              className="medium"
            >
              <option value="" disabled>
                Select a stop
              </option>
              {busRoutes[selectedRoute as keyof typeof busRoutes].map(
                (stop) => (
                  <option key={stop} value={stop} className="option">
                    {stop}
                  </option>
                )
              )}
            </select>
          </div>
        )}
      </div>
      {selectedRoute && selectedStop && (
        <div style={{ marginTop: "30px" }}>
          <h3>Map Placeholder</h3>
          <p>
            Displaying map for <strong>{selectedRoute}</strong> at{" "}
            <strong>{selectedStop}</strong>.
          </p>
          <div
            style={{
              width: "300px",
              height: "200px",
              backgroundColor: "#ddd",
              margin: "0 auto",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "1px solid #ccc",
            }}
          >
            Static Map Image
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
