import React from "react";
import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../component/Dropdown.css";

const Home: React.FC = () => {
  const busRoutes = {
    Gold: ["Stop A", "Stop B", "Stop C"],
    Red: ["Stop D", "Stop E", "Stop F"],
    Blue: ["Stop G", "Stop H", "Stop I"],
    Green: ["Stop J", "Stop K", "Stop L"],
  };

  const [selectedRoute, setSelectedRoute] = useState<string | null>(null);
  const [selectedStop, setSelectedStop] = useState<string | null>(null);

  // Define styles as objects
  const labelStyle: React.CSSProperties = {
    height: "154px",
    width: "837px",
    position: "relative",
    margin: "0 auto",
  };

  const textWrapperStyle: React.CSSProperties = {
    color: "#b3a369",
    fontFamily: '"Inter-Bold", Helvetica',
    fontSize: "64px",
    fontWeight: 700,
    letterSpacing: "-1.28px",
    lineHeight: "normal",
    position: "relative",
    textAlign: "center",
    margin: "100px auto",
    width: "837px",
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      {/* Title Section */}
      <div style={labelStyle}>
        <div style={textWrapperStyle}>
          Georgia Tech Stinger Shuttle Delay Visualizer
        </div>
      </div>

      {/* Description */}
      <p style={{ fontSize: "18px", marginBottom: "20px", marginTop: "160px" }}>
        Select a route and stop to visualize the shuttle delay information.
      </p>

      {/* Dropdown for Routes */}
      <div>
        <label htmlFor="route-select" style={{ fontSize: "18px" }}>
          Choose your bus line:
        </label>
        <br />
        <select
          id="route-select"
          onChange={(e) => {
            setSelectedRoute(e.target.value);
            setSelectedStop(null);
          }}
          value={selectedRoute || ""}
          style={{ marginTop: "10px", fontSize: "16px" }}
        >
          <option value="" disabled>
            Select a route
          </option>
          {Object.keys(busRoutes).map((route) => (
            <option key={route} value={route}>
              {route}
            </option>
          ))}
        </select>
      </div>

      {/* Dropdown for Stops */}
      {selectedRoute && (
        <div style={{ marginTop: "20px" }}>
          <label htmlFor="stop-select" style={{ fontSize: "18px" }}>
            Current stop:
          </label>
          <br />
          <select
            id="stop-select"
            onChange={(e) => setSelectedStop(e.target.value)}
            value={selectedStop || ""}
            style={{ marginTop: "10px", fontSize: "16px" }}
          >
            <option value="" disabled>
              Select a stop
            </option>
            {busRoutes[selectedRoute as keyof typeof busRoutes].map((stop) => (
              <option key={stop} value={stop}>
                {stop}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Map Placeholder */}
      {selectedRoute && selectedStop && (
        <div style={{ marginTop: "30px" }}>
          <h3>Map Placeholder</h3>
          <p>
            Displaying map for <strong>{selectedRoute}</strong> at{" "}
            <strong>{selectedStop}</strong>.
          </p>
          <div
            style={{
              width: "300px",
              height: "200px",
              backgroundColor: "#ddd",
              margin: "0 auto",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "1px solid #ccc",
            }}
          >
            Static Map Image
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
