import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Home.css";

const Home: React.FC = () => {
  const busRoutes = {
    Gold: ["Marta Midtown Station", "Tech Square WB", "5th Street Bridge WB"],
    Red: ["North Avenue Apts", "Tech Tower", "Transit Hub & Weber Building"],
    Blue: ["Paper Tricentennial", "West Village", "8th St & Hemphill Ave"],
    Green: ["575 14th Street", "14th St & State St", "GTRI Conference Center"],
    Clough: ["Clough Commons", "5th Street Bridge EB", "Tech Square EB"],
  };

  const [selectedRoute, setSelectedRoute] = useState<string | null>(null);
  const [selectedStop, setSelectedStop] = useState<string | null>(null);
  const [imageSrc, setImageSrc] = useState<string | null>("/data/default.png"); // Set default image initially

  const getImagePath = () => {
    if (selectedRoute) {
      const route = selectedRoute.replace(/\s+/g, "").toLowerCase();
      return `/data/${route}.png`; // Adjust the path based on your directory structure
    }
    return null;
  };

  const handleImageError = () => {
    setImageSrc("/data/default.png"); // Set the default image on error
  };

  const handleRouteChange = (route: string) => {
    setSelectedRoute(route);
    setSelectedStop(null);
    setImageSrc(getImagePath()); // Update the image source when the route changes
  };

  return (
    <div className="page">
      {/* Top Left Text */}
      <div className="top-left-text">GT Stinger Delay</div>

      {/* Title Section */}
      <div className="title-container">
        <div className="title-text">
          Georgia Tech Stinger Shuttle Delay Visualizer
        </div>
      </div>

      {/* Description */}
      <p
        style={{
          fontSize: "18px",
          color: "lightgray",
          marginBottom: "30px",
          marginTop: "30px",
        }}
      >
        A Georgia Tech's Big Data Big Impact project.
      </p>

      {/* Dropdown for Routes */}
      <div className="dropdown-container">
        <label htmlFor="route-select" className="dropdown-label">
          Choose your bus line
        </label>
        <select
          id="route-select"
          onChange={(e) => handleRouteChange(e.target.value)}
          value={selectedRoute || ""}
          style={{ marginTop: "10px", fontSize: "16px", width: "100%" }}
        >
          <option value="" disabled>
            Select a route
          </option>
          {Object.keys(busRoutes).map((route) => (
            <option key={route} value={route}>
              {route}
            </option>
          ))}
        </select>
      </div>

      {/* Dropdown for Stops */}
      {selectedRoute && (
        <div className="dropdown-container">
          <label htmlFor="stop-select" className="dropdown-label">
            Current stop
          </label>
          <select
            id="stop-select"
            onChange={(e) => setSelectedStop(e.target.value)}
            value={selectedStop || ""}
            style={{ marginTop: "10px", fontSize: "16px", width: "100%" }}
          >
            <option value="" disabled>
              Select a stop
            </option>
            {busRoutes[selectedRoute as keyof typeof busRoutes].map((stop) => (
              <option key={stop} value={stop}>
                {stop}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Route Image */}
      <div style={{ marginTop: "30px" }}>
        <p>
          Displaying map for <strong>{selectedRoute || "Default"}</strong>.
        </p>
        <div className="map-placeholder">
          <img
            src={imageSrc || ""}
            alt="Default route"
            style={{ width: "100%", height: "100%", objectFit: "cover" }}
            onError={handleImageError}
          />
        </div>
      </div>
    </div>
  );
};

export default Home;
