// src/features/routeView/RouteViewPage.tsx
import React, { useState, useEffect } from "react";
import styles from "./RouteViewPage.module.css";
import { Route } from "@/types/route";
import { Stop } from "@/types/stop";
import { DelayPrediction, LiveBusLocation } from "@/types/delay";
// import { mockRoutes } from "@/lib/mocks/mockTripData";
import { mockRoutes } from "../../lib/mocks/mockTripData";
import StopList from "./components/StopList";
import RouteMap from "./components/RouteMap";
import ETAInfo from "./components/ETAInfo";
import Dropdown from "react-bootstrap/Dropdown";

const RouteViewPage: React.FC = () => {
  const [routes, setRoutes] = useState<Route[]>([]);
  const [selectedRoute, setSelectedRoute] = useState<Route | null>(null);
  const [selectedStop, setSelectedStop] = useState<Stop | null>(null);

  useEffect(() => {
    // TODO: fetch real routes from API
    setRoutes(mockRoutes);
  }, []);

  useEffect(() => {
    if (selectedRoute) {
      // optionally fetch busLocations & delayPredictions for selectedRoute
    }
  }, [selectedRoute]);

  const handleRouteSelect = (routeId: string) => {
    const route = routes.find((r) => r.id === routeId) || null;
    setSelectedRoute(route);
    setSelectedStop(null);
  };

  const handleStopSelect = (stop: Stop) => {
    setSelectedStop(stop);
  };

  return (
    <div className={styles.container}>
      <h1>Route View</h1>
      <select
        onChange={(e) => handleRouteSelect(e.target.value)}
        value={selectedRoute?.id || ""}
      >
        <option value="">Select a route</option>
        {routes.map((r) => (
          <option key={r.id} value={r.id}>
            {r.name}
          </option>
        ))}
      </select>

      <Dropdown>
        <Dropdown.Toggle variant="primary" id="dropdown-basic">
          {selectedRoute ? `Route ${selectedRoute.name}` : "Select a Route"}
        </Dropdown.Toggle>

        <Dropdown.Menu>
          {routes.map((route) => (
            <Dropdown.Item
              key={route.id}
              eventKey={route.id}
              active={selectedRoute?.id === route.id}
            >
              {route.name}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>

      {selectedRoute && (
        <div className={styles.detailSection}>
          <div className={styles.listSection}>
            <StopList
              stops={selectedRoute.stops}
              selectedStopId={selectedStop?.id}
              onSelectStop={handleStopSelect}
            />
          </div>
          <div className={styles.mapSection}>
            <RouteMap
              stops={selectedRoute.stops}
              busLocations={selectedRoute.busLocations}
              pathCoordinates={selectedRoute.pathGeoJson}
            />
          </div>
          <div className={styles.infoSection}>
            <ETAInfo
              predictions={selectedRoute.delayPredictions || []}
              selectedStopId={selectedStop?.id}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default RouteViewPage;
