// src/features/routeView/components/RouteMap.tsx
import React, { useEffect } from "react";
import {
  MapContainer,
  TileLayer,
  Polyline,
  Marker,
  Popup,
} from "react-leaflet";
import { Stop } from "@/types/stop";
import { LiveBusLocation } from "@/types/delay";
//import bus_icon from "@/assets/Bus.png";
import bus_icon from "../../../assets/Bus.png";

interface RouteMapProps {
  stops: Stop[];
  busLocations?: LiveBusLocation[];
  pathCoordinates?: [number, number][];
}

const RouteMap: React.FC<RouteMapProps> = ({
  stops,
  busLocations = [],
  pathCoordinates,
}) => {
  // derive centre from first stop
  const center: [number, number] = stops.length
    ? [stops[0].latitude, stops[0].longitude]
    : [33.7756, -84.3963]; // fallback to GT campus

  return (
    <MapContainer
      center={center}
      zoom={15}
      style={{ height: "400px", width: "100%" }}
    >
      <TileLayer
        // using OSM tiles
        attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a>'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {pathCoordinates && <Polyline positions={pathCoordinates} color="blue" />}
      {stops.map((stop) => (
        <Marker key={stop.id} position={[stop.latitude, stop.longitude]}>
          <Popup>{stop.name}</Popup>
        </Marker>
      ))}
      {busLocations.map((bus) => (
        <Marker
          key={bus.busId}
          position={[bus.latitude, bus.longitude]}
          icon={bus_icon}
        >
          <Popup>
            Bus {bus.busId} on Route {bus.routeId}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default RouteMap;
